import React from 'react'

const ArticlesList = ({articles}) => (
    <>
        <h1>Article List</h1>
        {
            articles.map(
                (article, key)=>
                <Link className = "article-list-item" key={key} to = {"/article/"+article.name} >
                <h3>{article.title}</h3>
            <p>{article.content[0].substring(0, 150)}...</p>
                </Link>
            
            )
        }
        

    </>
);

export default ArticlesList;
